mapper.update:
UPDATE tb_amect
SET prepay_id = #{prepayId}
WHERE id = #{amectId} AND status = 1

update tb_amect
set status = #{status}
where uuid = #{uuid}
and status = 1

mapper.delete:
delete from tb_amect
where status != 2 and id in

    #{one}

mapper.select:
select a.id, a.uuid, u.name, d.dept_name as deptName, a.amount, a.reason, `at`.type, a.status, date_format(a.create_time, '%Y-%m-%d %H:%i') as createTime, if(a.user_id = #{currentUserId}, "true", "false") as mine from tb_amect a join tb_amect_type `at` on a.type_id = `at`.id join tb_user u on a.user_id = u.id join tb_dept d on u.dept_id = d.id
AND u.id = #{userId}
AND u.name LIKE '%${name}%'
AND u.dept_id = #{deptId}
AND at.id = #{typeId}
AND a.create_time BETWEEN #{startDate} AND #{endDate}
AND a.`status` = #{status}
order by a.id desc limit #{start}, #{length}

select count(*)
from tb_amect a
join tb_amect_type `at` on a.type_id = `at`.id
join tb_user u on a.user_id = u.id
join tb_dept d on u.dept_id = d.id


        AND u.id = #{userId}


        AND u.name LIKE '%${name}%'


        AND u.dept_id = #{deptId}


        AND at.id = #{typeId}


        AND a.create_time BETWEEN #{startDate} AND #{endDate}


        AND a.`status` = #{status}

select uuid,
       amount,
       prepay_id as prepayId,
       `status`
from tb_amect
where id = #{amectId}
and user_id = #{userId}

    and `status` = #{status}

select user_id as userId
from tb_amect
where uuid = #{uuid}

select status
from tb_amect
where id = #{amectId}
